sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	if (newValue === '') {
		return;
	}

	var WHAT_NEEDED = 'u_what_needed';
	var whatneeded = g_form.getValue(WHAT_NEEDED);

	g_form.clearOptions(WHAT_NEEDED);

	if (newValue == 'hr') {
		addOptions(WHAT_NEEDED, [
			['hr1','Human Resources 1'],
			['hr2', 'Human Resources 2'],
			['other', 'Other']]);
	} else if (newValue == 'facilities') {
		addOptions(WHAT_NEEDED, [
			['facilities1', 'Facilities 1'],
			['facilities2', 'Facilities 2'],
			['other', 'Other']
		]);
	} else if (newValue == 'legal') {
		addOptions(WHAT_NEEDED, [
			['legal1', 'Legal 1'],
			['legal2', 'Legal 2'],
			['other', 'Other']
		]);
	}

	if (isLoading && !g_form.isNewRecord()) {
		g_form.setValue(WHAT_NEEDED, whatneeded);
	}
}

function addOptions(field_name, options) {
	options.forEach(function(option) {
		g_form.addOption(field_name, option[0], option[1]);
	});
}
